    int a = 200;
    int i = 250;
    int count = 0;

    public void ClickS(View v) {
        if (a < i) {
            Toast.makeText(getApplicationContext(), "Smaller", Toast.LENGTH_LONG).show();
            i = i/2;
            count = count+1;
        }
    }

    public void ClickB(View v) {
        if (a < i) {
            Toast.makeText(getApplicationContext(), "Bigger", Toast.LENGTH_LONG).show();
            i = i/2;
            count = count+1;
        }
    }

    public void ClickBG(View v) {

            Toast.makeText(getApplicationContext(), "" + count, Toast.LENGTH_LONG).show();

    }

